 eyeVariance = random ( 0.05, 0.01);
 distribution = random( 1.0,  0.05);
 length = random( 0.4,0.8);
 lengthEvo = random( 1.3,  0.5);
 speedFactor = random( 0.1,  0.01);
 respawnFactor = random( 0.03,  0.005);
	createCanvas(700, 700);
  for(let i = 0 ; i < n ; i ++) {
