function draw() {
  background(0);
  person();
  if (currentSongIndex === 0) {
    Rainscene();
  } else if (currentSongIndex === 1) {
    EDM();
  } else if (currentSongIndex === 2) {
    Orange();
  }
}
function mouseClicked() {
  if (currentSongIndex !== -1) {
    songs[currentSongIndex].stop();
  }
  currentSongIndex = (currentSongIndex + 1) % songs.length;
  songs[currentSongIndex].play();
  isPlaying = true;
}

function Rainscene() {
  if (currentlighting) {
    currentlighting();
  }

  if (millis() > switchlighting) {
    let lightings = random([lighting1, lighting2]);
    currentlighting = lightings;
    switchlighting = millis() + random(1000, 2000);
  }

  for (let drop of rain) {
    drop.update();
    drop.display();
  }
  person();
}

function EDM() {
  if (lighttimer > 7) {
    lightvisible = !lightvisible;
    lighttimer = 0;
  }
  if (lightvisible) {
    backlight();
    bluelight();
  }
  lighttimer++;
  //lightshow();
  lasers.update();
  lasers.display();
  backlight();
  person();
}

