let y = 0;
let moveBubble = 0;
const maxHeight = 500
function setup() {
  createCanvas(400, 1500);
}

function draw() {
  background(0);

  if (keyIsPressed && key === "m") {
    bubble(200, 1500);
  }
}

function bubble(xCor, yCor) {
  y += 10;
  moveBubble = 5 * cos(frameCount / 2);

  noFill();
  strokeWeight(2);

  if (y > 0 && y < 800) {
    let opacity = map(y, 0, 800, 0, 255);
    stroke(255, opacity);
  } else if (y >= 800 && y < 1200) {
    stroke(255, 0);
  }

  circle(moveBubble + xCor, yCor - y, 15);
}

